
import json
from json.decoder import JSONDecodeError

class JSONSyntaxError(Exception):
    pass

# XXX: Deber√≠a capturar FileNotFoundError y retornar s=None o dejar que explote el script?
def load_json_file(filename):
    """
    Carga datos de un archivo JSON

    :param filename: Nombre del archivo JSON
    :param type: str
    :rtype: dict
    """

    if isinstance(filename, str):
        with open(filename) as json_file:
            try:
                data = json.load(json_file)
            except JSONDecodeError as e:
                msg = "Archivo '{}' con errores de sintaxis: {}".format(filename, e)
                raise JSONSyntaxError(msg)
        json_file.close()
    else:
        raise TypeError("Debe especificar el nombre del archivo JSON")
    
    return data

